import React, { useState, useEffect, useCallback, useMemo } from "react";
import axios from "axios";
import "./App.css";

function Chatbot() {
  const allPredefinedQuestions = useMemo(() => [
    "Who are you?",
    "What can you do?",
    "Tell me a joke!",
    "Where do you live?",
    "How can I contact you?",
    "What's your favorite color?",
    "What's the weather like?",
    "Can you give me advice?",
    "Tell me a fun fact!",
    "Do you like pizza?"
  ], []);

  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [predefinedQuestions, setPredefinedQuestions] = useState([]);
  const [askedQuestions, setAskedQuestions] = useState([]);
  const [questionStates, setQuestionStates] = useState({});
  const [questionCount, setQuestionCount] = useState(() => parseInt(localStorage.getItem("questionCount")) || 0);
  const [achievement, setAchievement] = useState(null);

  const getRandomQuestions = useCallback((excludedQuestions) => {
    const availableQuestions = allPredefinedQuestions.filter(q => !excludedQuestions.includes(q));
    const shuffled = availableQuestions.sort(() => 0.5 - Math.random());
    return shuffled.slice(0, 3);
  }, [allPredefinedQuestions]);

  useEffect(() => {
    setMessages([{ sender: "bot", text: "Hello! How can I help you today?" }]);
    setPredefinedQuestions(getRandomQuestions([]));
  }, [getRandomQuestions]);

  useEffect(() => {
    localStorage.setItem("questionCount", questionCount);
    checkAchievements(questionCount);
  }, [questionCount]);

  const checkAchievements = (count) => {
    if (count === 1) setAchievement("First question asked!");
    else if (count === 5) setAchievement("5 questions asked! Keep going!");
    else if (count === 10) setAchievement("10 questions! You're a pro!");
    else setAchievement(null);
  };

  const sendMessage = async (messageText) => {
    if (!messageText.trim()) return;

    setMessages(prev => [...prev, { sender: "user", text: messageText }]);
    setInput("");
    setQuestionCount(prev => prev + 1);

    setAskedQuestions(prev => {
      const newAskedQuestions = [...prev, messageText];
      setPredefinedQuestions(getRandomQuestions(newAskedQuestions));
      return newAskedQuestions;
    });

    setQuestionStates(prev => ({
      ...prev,
      [messageText]: 'fade-out',
    }));

    try {
      const API_URL = process.env.REACT_APP_API_URL || "http://localhost:7071/api/qna";
      const response = await axios.post(API_URL, { question: messageText });

      let botResponse = "Sorry, I didn't understand that.";

      if (typeof response.data === "string") {
        botResponse = response.data;
      } else if (response.data?.answers?.length > 0) {
        botResponse = response.data.answers[0].answer;
      }

      typeOutMessage(botResponse);
    } catch (error) {
      console.error("Error sending message:", error);
      typeOutMessage("Sorry, something went wrong.");
    }
  };

  const typeOutMessage = (fullMessage) => {
    let currentText = "";
    let index = 0;

    const interval = setInterval(() => {
      if (index < fullMessage.length) {
        currentText += fullMessage[index];
        setMessages(prev => {
          const newMessages = [...prev];
          if (newMessages.length > 0 && newMessages[newMessages.length - 1].sender === "bot") {
            newMessages[newMessages.length - 1].text = currentText;
          } else {
            newMessages.push({ sender: "bot", text: currentText });
          }
          return newMessages;
        });
        index++;
      } else {
        clearInterval(interval);
      }
    }, 50);
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") sendMessage(input);
  };

  return (
    <div className="chatbot-container">
      <div className="achievement-bar">
        <p>Next achievement in {Math.max(0, 5 - (questionCount % 5))} questions</p>
        {achievement && <div className="achievement-popup">{achievement}</div>}
      </div>

      <div className="predefined-questions">
        {predefinedQuestions.map((question, index) => (
          <button
            key={index}
            onClick={() => sendMessage(question)}
            className={`question-button ${questionStates[question] || ""}`}
            onAnimationEnd={() => {
              if (questionStates[question] === 'fade-out') {
                setQuestionStates(prev => ({
                  ...prev,
                  [question]: 'fade-in'
                }));
              }
            }}
          >
            {question}
          </button>
        ))}
      </div>

      <div className="chat-window">
        {messages.map((msg, index) => (
          <div key={index} className={`message-container ${msg.sender}`}>
            <div className={`message ${msg.sender}`}>{msg.text}</div>
          </div>
        ))}
      </div>

      <div className="input-container">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type your message here..."
        />
        <button onClick={() => sendMessage(input)}>Send</button>
      </div>
    </div>
  );
}

export default Chatbot;
